import { createContext, useState } from "react";
import axios from "axios";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [error, setError] = useState(null);

  // ðŸ”¹ Register
  const register = async (username, password) => {
    try {
      setError(null);
      await axios.post("http://localhost:5000/register", { username, password });
      alert("âœ… Registration successful! Please login.");
    } catch (err) {
      setError(err.response?.data?.message || "Registration failed");
    }
  };

  // ðŸ”¹ Login
  const login = async (username, password) => {
    try {
      setError(null);
      const res = await axios.post("http://localhost:5000/login", { username, password });
      setUser(res.data.username);
      fetchTasks(res.data.username);
    } catch (err) {
      setError(err.response?.data?.message || "Login failed");
    }
  };

  // ðŸ”¹ Logout
  const logout = () => {
    setUser(null);
    setTasks([]);
  };

  // ðŸ”¹ Fetch tasks
  const fetchTasks = async (username) => {
    const res = await axios.get(`http://localhost:5000/tasks/${username}`);
    setTasks(res.data);
  };

  // ðŸ”¹ Add task
  const addTask = async (title) => {
    try {
      setError(null);
      const res = await axios.post(`http://localhost:5000/tasks/${user}`, { title });
      setTasks([...tasks, res.data]);
    } catch (err) {
      setError(err.response?.data?.message || "Task creation failed");
    }
  };

  // ðŸ”¹ Update task
  const updateTask = async (id, title) => {
    try {
      setError(null);
      const res = await axios.put(`http://localhost:5000/tasks/${user}/${id}`, { title });
      setTasks(tasks.map((t) => (t.id === id ? res.data : t)));
    } catch (err) {
      setError(err.response?.data?.message || "Task update failed");
    }
  };

  // ðŸ”¹ Delete task
  const deleteTask = async (id) => {
    try {
      setError(null);
      await axios.delete(`http://localhost:5000/tasks/${user}/${id}`);
      setTasks(tasks.filter((t) => t.id !== id));
    } catch (err) {
      setError(err.response?.data?.message || "Task deletion failed");
    }
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        error,
        register,
        login,
        logout,
        tasks,
        addTask,
        updateTask,
        deleteTask,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
