import { useContext, useState } from "react";
import { AuthContext } from "../context/AuthContext";

export default function AuthForms() {
  const { register, login, user, error } = useContext(AuthContext);
  const [form, setForm] = useState({ username: "", password: "" });

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const validateAndRegister = () => {
    if (!form.username || !form.password) return alert("All fields required");
    if (form.password.length < 4) return alert("Password must be at least 4 chars");
    register(form.username, form.password);
  };

  const validateAndLogin = () => {
    if (!form.username || !form.password) return alert("All fields required");
    login(form.username, form.password);
  };

  if (user) return null; // hide login/register if logged in

  return (
    <div>
      <h2>Register</h2>
      <input name="username" placeholder="Username" onChange={handleChange} />
      <input name="password" placeholder="Password" type="password" onChange={handleChange} />
      <button onClick={validateAndRegister}>Register</button>

      <h2>Login</h2>
      <input name="username" placeholder="Username" onChange={handleChange} />
      <input name="password" placeholder="Password" type="password" onChange={handleChange} />
      <button onClick={validateAndLogin}>Login</button>

      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
