import { useContext, useState } from "react";
import { AuthContext } from "../";

export default function AuthForms() {
  const { register, login, user, error } = useContext(AuthContext);
  const [registerForm, setRegisterForm] = useState({
    name: "",
    phone: "",
    email: "",
    username: "",
    password: "",
  });
  const [loginForm, setLoginForm] = useState({ username: "", password: "" });

  const handleRegisterChange = (e) =>
    setRegisterForm({ ...registerForm, [e.target.name]: e.target.value });

  const handleLoginChange = (e) =>
    setLoginForm({ ...loginForm, [e.target.name]: e.target.value });

  const validateEmail = (email) =>
    /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  const handleRegister = () => {
    if (!registerForm.name || !registerForm.phone || !registerForm.email || !registerForm.username || !registerForm.password) {
      return alert("All fields are required");
    }
    if (!validateEmail(registerForm.email)) return alert("Invalid email format");
    if (registerForm.password.length < 4) return alert("Password must be at least 4 chars");
    register(registerForm);
  };

  const handleLogin = () => {
    if (!loginForm.username || !loginForm.password) return alert("All fields required");
    login(loginForm.username, loginForm.password);
  };

  if (user) return null;

  return (
    <div>
      <h2>Register</h2>
      <input name="name" placeholder="Name" onChange={handleRegisterChange} />
      <input name="phone" placeholder="Phone" onChange={handleRegisterChange} />
      <input name="email" placeholder="Email" onChange={handleRegisterChange} />
      <input name="username" placeholder="Username" onChange={handleRegisterChange} />
      <input name="password" type="password" placeholder="Password" onChange={handleRegisterChange} />
      <button onClick={handleRegister}>Register</button>

      <h2>Login</h2>
      <input name="username" placeholder="Username" onChange={handleLoginChange} />
      <input name="password" type="password" placeholder="Password" onChange={handleLoginChange} />
      <button onClick={handleLogin}>Login</button>

      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
